<?php/*Copyright (c) 2013 Esscotti Ltd, All Rights Reservedhttp://www.blockmanagementonline.com/ - Block Management Online*/class bmo_shortcodes {	function __construct(){		add_shortcode('bmo-portal', array($this, 'shortcode_bmo_portal'));	}		function shortcode_bmo_portal($atts, $content=NULL) {		global $bmo;		if (isset($_GET['area'])) {			$area = $_GET['area'];		} else {			if (is_array($atts) && array_key_exists('area', $atts)) {				$area = $atts['area'];			} else {				$area = '';			}		}		if (isset($_GET['page'])) {			$page = $atts['page'];		} else {			if (is_array($atts) && array_key_exists('page', $atts)) {				$page = $atts['page'];			} else {				$page = '';			}		}		return $bmo->view($area, $page);	}		}?>